import React, { createContext, useState } from "react";
import uuid from "uuid/v1";

export const SongContext = createContext();

const SongContextProvider = props => {
  const [songs, setSongs] = useState([
    { title: "Aquarela", singer: "toquinho", id: 1 },
    { title: "Shout", singer: "Tears for fears", id: 2 },
    { title: "the mask and the mirror", singer: "Loreena McKennitt", id: 3 },
    { title: "castles in the air", singer: "Don McLean", id: 4 }
  ]);

  const addSong = (title, singer) => {
    setSongs([...songs, { title, singer, id: uuid() }]);
  };
  const removeSong = () => {};

  const clearAll = songs => {
    setTitle("");
    setSinger("");
  };
  const editSong = () => {};

  return (
    <SongContext.Provider
      value={{ songs, addSong, removeSong, clearAll, editSong }}
    >
      {props.children}
    </SongContext.Provider>
  );
};

export default SongContextProvider;
