import uuid from 'uuid/v4';

const songReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_BOOK':
      return [
        ...state,
        {
          title,
          singer,
          id: uuid()
        }
      ];

    case 'REMOVE_BOOK':
      return state.filter(song => song.id !== id);
    // o "state" se refere aos books
    default:
      return state;
  }
};

export default songReducer;
