import React, { useContext, useState } from "react";
import { SongContext } from "../contexts/SongContext";
import SongList from "./SongList";

const SongForm = () => {
  const { songs } = useContext(SongContext);
  const { addSong } = useContext(SongContext);
  const { setSongs } = useContext(SongContext);
  const [title, setTitle] = useState("");
  const [singer, setSinger] = useState("");

  const handleSubmit = e => {
    e.preventDefault();
    addSong(title, singer);
    //limpar input após adicionar a canção
    setTitle("");
    setSinger("");
  };

  const clearAll = songs => {
    setTitle("");
    setSinger("");
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="song title..."
          required
          value={title}
          onChange={e => setTitle(e.target.value)}
        />

        <input
          type="text"
          placeholder="song singer..."
          required
          value={singer}
          onChange={e => setSinger(e.target.value)}
        />

        <input type="submit" value="add a song" />
      </form>
      {songs.length > 1 && (
        <button className="remove-all" onClick={() => clearAll(songs)}>
          remove all songs
        </button>
      )}
    </div>
  );
};

export default SongForm;
